import { FatalError } from '@vercel/workflow';

type DeepgramParams = {
  audio_url: string;
  model?: string;
  language?: string;
};

export async function deepgramTranscribe(params: DeepgramParams) {
  'use step';

  const apiKey = process.env.DEEPGRAM_API_KEY;

  if (!apiKey) {
    throw new FatalError('DEEPGRAM_API_KEY is required');
  }

  const model = params.model || 'nova-2';
  const language = params.language || 'en';

  const response = await fetch(
    `https://api.deepgram.com/v1/listen?model=${model}&language=${language}`,
    {
      method: 'POST',
      headers: {
        Authorization: `Token ${apiKey}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        url: params.audio_url,
      }),
    }
  );

  if (!response.ok) {
    throw new FatalError(`Deepgram API error: ${response.status}`);
  }

  const data = await response.json();
  return data.results.channels[0].alternatives[0].transcript;
}
